#!/bin/bash
echo $HIVE1_ID, $DATE, $HIVE1_WEIGHT, $HIVE1_TEMP, $HIVE1_HUMIDITY, $HIVE1_AMBIENT_TEMP, $HIVE1_AMBIENT_HUMIDITY
/usr/bin/mysql -u root -praspberry -e "USE hivetool_raw; INSERT INTO HIVE_DATA (hive_id, hive_observation_time_local, hive_weight_lbs, hive_temp_c, hive_humidity, ambient_temp_c, ambient_humidity, wx_temp_f,wx_temp_c, wx_relative_humidity, wx_wind_dir, wx_wind_mph,wx_wind_gust_mph,wx_pressure_mb, wx_dewpoint_f, wx_precip_1hr_in, wx_precip_today_in) values ($HIVE_ID, '$DATE', $HIVE_WEIGHT, $HIVE_TEMP, $HIVE_HUMIDITY, $AMBIENT_TEMP, $AMBIENT_HUMIDITY, $WX_TEMP_F, $WX_TEMP_C, $WX_RELATIVE_HUMIDITY, '$WX_WIND_DIR', $WX_WIND_MPH, $WX_WIND_GUST_MPH, $WX_PRESSURE_MB, $WX_DEWPOINT_F, $WX_PRECIP_1HR_IN, $WX_PRECIP_TODAY_IN);UPDATE HIVE_PARAMETERS SET last_update='$DATE'"
